import 'package:universal_html/html.dart';

class StaticData {
  static bool isLoggedIn = false;
  static String email = '';
  static int sellerEventId = 0;
  static String accessToken = '';
  static int userId = 0;
  static String appLanguage = 'fr'; // Default value
  static const String appLanguageKey = 'Language'; // Key for SharedPreferences

  static String defultImage =
      'data:image/png;base64,';
  static void saveLoginStatus(bool status) {
    window.localStorage['loggedIn'] = status.toString();
    isLoggedIn = status;
  }

  // Method to check login status from localStorage
  static bool getLoginStatus() {
    String? loggedIn = window.localStorage['loggedIn'];
    return loggedIn == 'true'; // Return true if loggedIn is 'true'
  }

  //!
  static String role = '';
}
